import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, boolean, real } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const buses = pgTable("buses", {
  id: varchar("id").primaryKey(),
  busNumber: text("bus_number").notNull(),
  route: text("route").notNull(),
  capacity: integer("capacity").notNull().default(40),
  currentPassengers: integer("current_passengers").notNull().default(0),
  status: text("status").notNull().default("active"), // active, maintenance, offline
  location: text("location"),
  lastUpdate: timestamp("last_update").defaultNow(),
  isActive: boolean("is_active").notNull().default(true),
  alertThreshold: integer("alert_threshold").notNull().default(35),
});

export const stations = pgTable("stations", {
  id: varchar("id").primaryKey(),
  name: text("name").notNull(),
  location: text("location").notNull(),
  waitingPassengers: integer("waiting_passengers").notNull().default(0),
  lastUpdate: timestamp("last_update").defaultNow(),
  isActive: boolean("is_active").notNull().default(true),
});

export const passengerData = pgTable("passenger_data", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  busId: varchar("bus_id").references(() => buses.id),
  stationId: varchar("station_id").references(() => stations.id),
  passengersIn: integer("passengers_in").notNull().default(0),
  passengersOut: integer("passengers_out").notNull().default(0),
  timestamp: timestamp("timestamp").defaultNow(),
});

export const alerts = pgTable("alerts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  busId: varchar("bus_id").references(() => buses.id),
  alertType: text("alert_type").notNull(), // capacity, maintenance, offline
  message: text("message").notNull(),
  severity: text("severity").notNull().default("medium"), // low, medium, high, critical
  isRead: boolean("is_read").notNull().default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

export const activityLog = pgTable("activity_log", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  busId: varchar("bus_id").references(() => buses.id),
  activity: text("activity").notNull(),
  description: text("description"),
  timestamp: timestamp("timestamp").defaultNow(),
});

// Insert schemas
export const insertBusSchema = createInsertSchema(buses).omit({ 
  lastUpdate: true 
});

export const insertStationSchema = createInsertSchema(stations).omit({ 
  lastUpdate: true 
});

export const insertPassengerDataSchema = createInsertSchema(passengerData).omit({ 
  id: true,
  timestamp: true 
});

export const insertAlertSchema = createInsertSchema(alerts).omit({ 
  id: true,
  createdAt: true 
});

export const insertActivityLogSchema = createInsertSchema(activityLog).omit({ 
  id: true,
  timestamp: true 
});

// Passenger count update schema
export const passengerCountUpdateSchema = z.object({
  busId: z.string(),
  currentPassengers: z.number().min(0),
  passengersIn: z.number().min(0),
  passengersOut: z.number().min(0),
  location: z.string().optional(),
});

// Types
export type Bus = typeof buses.$inferSelect;
export type Station = typeof stations.$inferSelect;
export type PassengerData = typeof passengerData.$inferSelect;
export type Alert = typeof alerts.$inferSelect;
export type ActivityLog = typeof activityLog.$inferSelect;

export type InsertBus = z.infer<typeof insertBusSchema>;
export type InsertStation = z.infer<typeof insertStationSchema>;
export type InsertPassengerData = z.infer<typeof insertPassengerDataSchema>;
export type InsertAlert = z.infer<typeof insertAlertSchema>;
export type InsertActivityLog = z.infer<typeof insertActivityLogSchema>;
export type PassengerCountUpdate = z.infer<typeof passengerCountUpdateSchema>;

utils/thread.py.content:
import cv2, threading, queue

class ThreadingClass:
  # initiate threading class
  def __init__(self, name):
    self.cap = cv2.VideoCapture(name)
    # define an empty queue and thread
    self.q = queue.Queue()
    t = threading.Thread(target=self._reader)
    t.daemon = True
    t.start()

  # read the frames as soon as they are available
  # this approach removes OpenCV's internal buffer and reduces the frame lag
  def _reader(self):
    while True:
      ret, frame = self.cap.read() # read the frames and ---
      if not ret:
        break
      if not self.q.empty():
        try:
          self.q.get_nowait()
        except queue.Empty:
          pass
      self.q.put(frame) # --- store them in a queue (instead of the buffer)

  def read(self):
    return self.q.get() # fetch frames from the queue one by one

  def release(self):
    return self.cap.release() # release the hw resource

